package a.gautham.tasker;import android.content.Context;import android.view.Gravity;import android.widget.Toast;public class Common {    public static final String FlagsPref = "Flags";    public static final String NewUser = "NewUser";    public static void toastShort(Context context, String msg){        Toast.makeText(context,msg,Toast.LENGTH_SHORT).show();    }    public static void toastLong(Context context, String msg){        Toast.makeText(context,msg,Toast.LENGTH_LONG).show();    }    public static void toastShort(Context context, String msg, int gravity){        Toast toast = Toast.makeText(context,msg, Toast.LENGTH_LONG);        toast.setGravity(gravity,0,0);        toast.show();    }    public static void toastLong(Context context, String msg, int gravity){        Toast toast = Toast.makeText(context,msg, Toast.LENGTH_LONG);        toast.setGravity(gravity,0,0);        toast.show();    }}